var CQ_collab_ratings_defaultMessage = ""; // overlay in page

function CQ_collab_ratings_setRating(id, rating) {
    document.getElementById(id + "-rating").value = (rating * 20);
    var i = 1;
    while (i <= 5) {
        document.getElementById(id + "-star-" + i).className =
            "ratings-star " + (i <= rating ? "full" : "empty");
        i++;
    }
}

// comments overrides

function CQ_collab_comments_validateFields(id) {
    // Validate text
    var tField = document.getElementById(id + "-text");
    var rField = document.getElementById(id + "-rating");
    if (tField) {
        if ((tField.value == "" || tField.value == CQ_collab_comments_getDefaultMessage(id)) && (!rField || !rField.value)) {
            if (rField) {
                CQ_collab_comments_showError(CQ.I18n.getMessage("Please provide either a rating or a comment"), id + "-text");
            } else {
                CQ_collab_comments_showError(CQ.I18n.getMessage("Please provide a comment"), id + "-text");
            }
            return false;
        }
    } else if (!rField.value) {
        CQ_collab_comments_showError(CQ.I18n.getMessage("Please provide a rating"), id + "-text");
        return false;
    }
    if (tField && tField.value == CQ_collab_comments_getDefaultMessage(id)) {
        tField.value = "";
    }
    return true;
}
